const text = $input.first().json.command_text;
const parts = text.split(' ');


// GET COMMAND
// for example /cloudbot info mutasem
// should return "info"
const command = parts[0];


// GET FLAGS 
// for example /cloudbot info mutasem --test --flag
// should return ['--test', '--flag']
const flags = parts.filter((part) => part.startsWith('--'));


// GET PARAMS
// for example /cloudbot info mutasem test
// should return ["mutasem", "test"]
let params = parts
  .filter((part, i) => i > 0 && !part.startsWith('--'));
params = params.filter((param, i) => {
  if (param === '-e') {
    return false;
  }
  if (params[i - 1] === '-e') {
    return false;
  }

  return true;
});


// GET ENV VARS
// for example /cloudbot info mutasem -e env=prod
// should return {env: "prod"}
const env = parts.filter((val, i) => {
  return i > 0 && parts[i - 1] === '-e';
})
  .reduce((accu, opt) => {
  if (!opt.includes('=')) {
    return accu;
  }

  const key = opt.split('=')[0];
  const val = opt.split('=')[1];

  accu[key] = clean(val);
  return accu;
}, {});

// Add workflow to run
const commands = $input.first().json.commands;
let workflow;
if (commands[command]) {
  workflow = commands[command];
}

return {
  ...$input.first().json,
  command,
  flags,
  env,
  params,
  workflow,
}

function clean(str) {
  return str.replaceAll(`‘`, '\'').replaceAll('“', '"').replaceAll('”', '"').replaceAll('’', '\'');
}

const SIGNING_SECRET = $input.first().json.slack_secret_signature;
const item = $('Webhook to call for Slack command').first();

if (!item.binary) {
  throw new Error('Missing binary data');
}

const crypto = require('crypto');
const { binary: { data } } = item;

if (
  !item.json.headers['x-slack-request-timestamp'] ||
  Math.abs(
    Math.floor(new Date().getTime() / 1000) -
      +item.json.headers['x-slack-request-timestamp']
  ) > 300
) {
  throw new Error('Unauthorized, request not fresh');
}

const rawBody = Buffer.from(data.data, 'base64').toString()

// compute the basestring
const baseStr = `v0:${item.json.headers['x-slack-request-timestamp']}:${rawBody}`;

// extract the received signature from the request headers
const receivedSignature = item.json.headers['x-slack-signature'];

const expectedSignature = `v0=${crypto.createHmac('sha256', SIGNING_SECRET)
.update(baseStr, 'utf8')
.digest('hex')}`;

// match the two signatures
if (expectedSignature !== receivedSignature) {
throw new Error('Unauthorized, umatched signatures');
}

return $input.all();

