function escapeRedisJsonSyntax(value) {
  if (typeof value === 'string') {
    return value.replace(/["\\/]/g, '\\$&');
  }
  return value;
}

const outputItems = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const escapedItem = { TG_USER_: {} };

  for (const key in item) {
    const value = item[key];
    if (Array.isArray(value)) {
      escapedItem.TG_USER_[key] = [escapeRedisJsonSyntax(value[0])];
    } else if (typeof value === 'object') {
      flattenObject(value, escapedItem.TG_USER_, key);
    } else {
      escapedItem.TG_USER_[key] = escapeRedisJsonSyntax(value);
    }
  }

  outputItems.push(escapedItem);
}

function flattenObject(obj, result, prefix) {
  for (const key in obj) {
    const newKey = prefix ? `${prefix}_${key}` : key;
    const value = obj[key];
    if (typeof value === 'object') {
      if (Array.isArray(value)) {
        result[newKey] = [escapeRedisJsonSyntax(value[0])];
      } else {
        flattenObject(value, result, newKey);
      }
    } else {
      result[newKey.replace('json_message_', '').replace('json_', '')] = escapeRedisJsonSyntax(value);
    }
  }
}

return outputItems;

